import React from 'react';

window.confirm('SUPPRESSION!!!',{
  confirmLabel: 'OUI',
  abortLabel: 'NON'
})

const Tableau = props => (
      <table>
      <thead>
        <tr>
          <th className="act">id</th>
          <th>Nom</th>
          <th>Pr√©nom</th>
          <th className="act">Actions</th>
        </tr>
      </thead>
      <tbody>
        {props.users.length > 0 ? (
          props.users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.nom}</td>
              <td>{user.prenom}</td>
              <td>
          <button className="btn btn-danger" onClick={
            () => {if(window.confirm){props.suppr(user.id)}
            }}>X</button>
              </td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan={4}>Aucun enregistrement</td>
          </tr>
        )}
      </tbody>
      </table>
)


export default Tableau;
