import React, { useState } from 'react';
import Tableau from './Component/Tableau/Tableau';
import Form from './Component/Form/Form';

import './App.css';
import './Component/Form/Form.css'
import './Component/Tableau/Tableau.css'

const App = () => {
    const usersData = []

    const [ users, setUsers ] = useState(usersData)

    const ajouter = user => {
      user.id = users.length
      setUsers([ ...users, user ])
    }

    const suppr = id => {
        setUsers(users.filter(user => user.id !== id))
    }

    $(function() {
      $('a[data-confirm]').click(function(ev) {
        var href = $(this).attr('href');

        if (!$('#dataConfirmModal').length) {
          $('body').append('<div id="dataConfirmModal" class="modal" role="dialog" aria-labelledby="dataConfirmLabel" aria-hidden="true"><div class="modal-dialog"><div class="modal-content"><div class="modal-header"><h3 id="dataConfirmLabel" style="text-align:center">Confimer la suppression ?</h3></div><div class="modal-body"></div><div class="modal-footer"><button class="btn" data-dismiss="modal" aria-hidden="true">Non</button><a class="btn btn-danger" id="dataConfirmOK">Oui</a></div></div></div></div>');
        }
        $('#dataConfirmModal').find('.modal-body').text($(this).attr('data-confirm'));
        $('#dataConfirmOK').attr('href', href);
        $('#dataConfirmModal').modal({show:true});

        return false;
      });
    });
    return (
      <div className="container bg-danger">
      <div className="flex-row">
        <div className="flex-large">
          <Form ajouter={ajouter} />
        </div>
        <div className="flex-large">
          <Tableau users={users} suppr={suppr}/>
        </div>
      </div>
    </div>
    )
  };

export default App;
