    return (
      <div>
        <button onClick={() => props.suppr(user.id)}>OUI</button>
        <PopupboxContainer />
      </div>
    )
  }
}

const Tableau = props => (
      <table>
      <thead>
        <tr>
          <th className="act">id</th>
          <th>Nom</th>
          <th>Prénom</th>
          <th className="act">Actions</th>
        </tr>
      </thead>
      <tbody>
        {props.users.length > 0 ? (
          props.users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.nom}</td>
              <td>{user.prenom}</td>
              <td>
          <button className="btn btn-danger" onClick={PopupboxManager}>X</button>
              </td>
