import React from 'react';

const Tableau = props => (
      <table>
      <thead>
        <tr>
          <th className="act">id</th>
          <th>Nom</th>
          <th>Pr√©nom</th>
          <th className="act">Actions</th>
        </tr>
      </thead>
      <tbody>
        {props.users.length > 0 ? (
          props.users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.nom}</td>
              <td>{user.prenom}</td>
              <td>
          <button className="btn btn-danger" onClick={this.submit}>X</button>
              </td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan={4}>Aucun enregistrement</td>
          </tr>
        )}
      </tbody>
      </table>
)
submit = () => {
  confirm({
    title: 'Confirm to submit',
    message: 'Are you sure to do this.',
    buttons: [
      {
        label: 'Yes',
        onClick: () => {if(window.confirm('Confirmer la Suppression?')){props.suppr(user.id)}}
      },
      {
        label: 'No',
        onClick: () => alert('Click No')
      }
    ]
  })
};


export default Tableau;
