import React, { useState } from 'react';
import Tableau from './Component/Tableau/Tableau';
import Form from './Component/Form/Form';
import Popup from "reactjs-popup";
import './App.css';
import './Component/Form/Form.css'
import './Component/Tableau/Tableau.css'

const App = () => {
    const usersData = [
      { id: 1, nom: 'Tania', prenom: 'floppydiskette' },
      { id: 2, nom: 'Craig', prenom: 'siliconeidolon' },
      { id: 3, nom: 'Ben', prenom: 'benisphere' }
    ]

    const [ users, setUsers ] = useState(usersData)

    const ajouter = user => {
      user.id = users.length + 1
      setUsers([ ...users, user ])
    }

    const suppr = id => {
      if (confirm("Confirmer la suppression?")) {
        setUsers(users.filter(user => user.id !== id))
      }
    }

    return (
      <div className="container">
      <div className="flex-row">
        <div className="flex-large">
          <Form ajouter={ajouter} />
        </div>
        <div className="flex-large">
          <Tableau users={users} suppr={suppr}/>
        </div>
      </div>
    </div>
    )
  };

export default App;
